//requestをrequire
var request = require('request');
request_count = 2000
//オプションを定義
  var options = {
    url: 'http://localhost:3000/',
    json: true,
    }

//リクエスト送信
for (var i = 0 ; i<request_count ; i++){
  request(options, function (error, response, body) {
		if(error) console.log(error);
  	else console.log(body);
  });
}
